import React, { useState, useEffect } from "react"

import Search from "./Header/Search"
import HeaderWrapper from "./Header/HeaderWrapper"

const Header = () => {
  const [isSticky, setIsSticky] = useState(false);
  const sticky = document.getElementById("header-section").offsetTop;

  const handlerSticky = () =>{
    setIsSticky(window.pageYOffset > sticky);
  }

  useEffect(() => {
    const watchScroll = () => {
      window.addEventListener("scroll", handlerSticky);
    }
    watchScroll();

    return () => {
      window.removeEventListener("scroll", handlerSticky);
    }
  });

  return (
    <>
      <header className={`header-section ${!isSticky ? "header-transparent" : ""}`} id="header-section">
        <HeaderWrapper/>
      </header>
      <Search/>
    </>
  )
}

export default Header
